import React, { Fragment, useState, useEffect } from "react";

const ListTodos = () => {
    // usestate lets you set up empty state
    const [todos, setTodos] = useState([]);

    async function getTodos() {
        const res = await fetch("http://localhost:8000/todos");
        const todoDict = await res.json();
        const todoArray = todoDict.data;
        setTodos(todoArray);
    }

    useEffect(() => {
        getTodos();
      }, []);

      console.log(todos);

    return (
        <Fragment>
        {" "}
        <table class="table mt-5">
          <thead>
            <tr>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>

            {todos.map((todo) => (
              <tr key={todo.id}>
                <td>{todo.item}</td>
                <td>
                  {/* <EditTodo todo={todo} /> */}
                  <p>PH, edit todo button</p>
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    // onClick={() => deleteTodo(todo.todo_id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </Fragment>
    );
}

export default ListTodos;
