import React, { Fragment, useState, useEffect } from "react";
import EditTodo from "./EditTodo";


// const TodosContext = React.createContext({
//     todos: [], getTodos: () => {}
// })

const ListTodos = () => {
    // usestate lets you set up empty state
    // created an empty state variable array, todos, and a state method, settodos, so we can update the state variable
    const [todos, setTodos] = useState([]);

    // defined a function getTodos to retrieve todos from the backend asynchronously and update the todo state variable
    async function getTodos() {
        const res = await fetch("http://localhost:8000/todos");
        const todos = await res.json();
        setTodos(todos.data);
    }

    useEffect(() => {
        getTodos();
      }, []);

    //   console.log(todos);



      // render the data by iterating through the todos state variable

    return (
        // <TodosContext.Provider value={{todos, getTodos}}>
        <Fragment>
        <table class="table mt-5">
          <thead>
            <tr>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>

            {todos.map((todo) => (
              <tr key={todo.id}>
                <td>{todo.item}</td>
                <td>
                  <EditTodo todo={todo} />
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    // onClick={() => deleteTodo(todo.todo_id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        </Fragment>
    //   </TodosContext.Provider>
    );
}

export default ListTodos;
