        <InputTodo todos={todos}/>
        <table className="table mt-5">
          <thead>
            <tr>
              <th>Description</th>
              <th>Edit</th>
              <th>Delete</th>
            </tr>
          </thead>
          <tbody>

            {todos.map((todo) => (
              <tr key={todo.id}>
                <td>{todo.item}</td>
                <td>
                  <EditTodo todo={todo} />
                </td>
                <td>
                  <button
                    className="btn btn-danger"
                    onClick={() => deleteTodo(todo.id)}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        </Fragment>
    //   </TodosContext.Provider>
    );
}

export default ListTodos;
